import { Component } from '@angular/core';
import {MatButtonModule} from '@angular/material/button';
import { MICROSERVICE_PRODUITS_API_URL } from 'src/app/constants';

@Component({
  selector: 'app-produits-content',
  templateUrl: './produits-content.component.html',
  styleUrls: ['./produits-content.component.css'],
  standalone: true,
  imports: [MatButtonModule],
})
export class ProduitsContentComponent {
  titre: string = '';
  description: string = '';
  image: string = '';
  prix: number = '';

  constructor() { }

  onSubmit() {
    const formData = {
      titre: this.titre,
      description: this.description,
      image: this.image,
      prix: this.prix
    };

    fetch(MICROSERVICE_PRODUITS_API_URL+'/produits/ajouter', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Erreur lors de la requête.');
        }
        return response.json();
      })
      .then(data => {
        alert('Groupe enregistré avec succès !');
        console.log('Réponse du serveur:', data);
        // Réinitialiser le formulaire après l'envoi des données avec succès
        this.titre = '';
        this.description = '';
        this.image = '';
        this.prix = 0;
      })
      .catch(error => {
        alert('Erreur lors de l\'enregistrement du groupe !');
        console.error('Erreur lors de l\'envoi des données:', error);
      });
  }
}
